import React from "react";
export default class Modal extends React.Component {

  onClose = () => {
    this.props.onClose && this.props.onClose();
  }


  render() {
    if(!this.props.show){
          return null;
      }

    return (
      <React.Fragment>
      <div className="Modal">
      <p >Hello Modal {this.props.prize}</p>
      <button> Yes </button>
      <button onClose={this.onClose()} >Close</button>
      </div>
      </React.Fragment>
    )
  }
}
